task main()
{
	clearTimer(T1);
	clearTimer(T2);
	while(true)
	{
		//Sets a boolean to one if the button is pressed or zero if it is not pressed
		if(SensorValue[button] == 1)
			buttonPressed = true;
		else
			buttonPressed = false;

		//If boolean "buttonPressed" is true, the motor starts, the integer "iterations" is set to zero, the timer is cleared, and the boolean is set to false. If boolean "buttonPressed" is false and the integer "iterations" is greater than or equal to 4, the motor is stopped and the timer is cleared again.
		if(buttonPressed)
		{
			iterations = 0;
			motor[TestMotor] = 127;
			clearTimer(T1);
			buttonPressed = false;
			SensorValue(encoder) = 0;
		}	else if(!buttonPressed && iterations >= 5) {
			motor[TestMotor] = 0;
			buttonPressed = false;
			if(iterations == 5)
				average();
			iterations = 6;
		}

		if(time1[T1] >= 1000 && iterations < 5)
		{
			rpsReadings[iterations] = SensorValue[encoder];
			iterations++;
			clearTimer(T1);
			SensorValue(encoder) = 0;
		}

		//flashLight();
		if(rps <= 799)
		{
			turnLEDOn(dgtl12);
			turnLEDOff(dgtl10);
			turnLEDOff(dgtl8);
		} else if(rps >= 800 & rps <= 1299)
		{
			turnLEDOff(dgtl12);
			turnLEDOn(dgtl10);
			turnLEDOff(dgtl8);
		} else if(rps >= 1300)
		{
			turnLEDOff(dgtl12);
			turnLEDOff(dgtl10);
			turnLEDOn(dgtl8);
		} else
		{
			turnLEDOff(dgtl12);
			turnLEDOff(dgtl10);
			turnLEDOff(dgtl8);
		}
	}
}
